package desafio.ubot.avaliafilmes.model.document;import jakarta.validation.constraints.Max;import jakarta.validation.constraints.Min;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import org.hibernate.validator.constraints.Range;import org.springframework.beans.factory.annotation.Value;import org.springframework.data.annotation.Id;import org.springframework.data.mongodb.core.mapping.Document;@Data@Document@NoArgsConstructor@AllArgsConstructorpublic class Filme {    public final static String sequencia = "filme";    @Id    private String id;    private Integer filmeId;    private String titulo;    private String descricao;    @Range(min=30,max=2000,message="A qde de liquido varia entre 30ml e 2000ml")    private Integer nota;}